import React,{useState} from 'react';
import Addlist from '../Componentthree/addlist';
import List from '../Componenttwo/list';
import './todolist.css'


const dolist =[
    {id:1, title:'learn', done:true},
    {id:2, title:'write', done:false}
]
const Todolist = () =>{
    const [todos , setTodos] = useState(dolist)
    const addnew =  (newadd) => {
        const newlist=[todos,newadd]
        setTodos(newlist)
    }
    return <div className='todo'>          
        <h1 className='Head'>Todo List</h1>
        <Addlist add = {addnew}/>  
        {todos.map((todo) => <List items={todo}/>)}        
    </div>
}

export default Todolist;
